import jsondef _getSSID_PSW():        # reading the data from the file        with open('config.txt') as f:            data = f.read()          #print("Data type before reconstruction : ", type(data))        # reconstructing the data as a dictionary        js = json.loads(data)        #print("Data type after reconstruction : ", type(js))        #print(js)        ssid = js["SSID"]        psw  = js["PSW"]        return ssid, psw  from network import WLANimport networkclass WIFI():            def __init__(self, ssid, psw):        self.wlan = network.WLAN(network.STA_IF)        if not self.wlan.isconnected():            print('connecting to network...')            self.wlan.active(True)            if ssid == "":                ssid,psw = _getSSID_PSW()                #print(ssid)                #print(psw)            self.wlan.connect(ssid, psw)            while not self.wlan.isconnected():                pass            self.wlan.config(dhcp_hostname="m5stickcp")            print('network config:', self.wlan.ifconfig())            def show(self):        print(self.wlan.ifconfig())        print(self.wlan.config("dhcp_hostname"))if __name__ == "__main__":               wlan = WIFI('','')    wlan.show()    print("WIFI")